import React from "react";
import classNames from "classnames";
import { SectionProps } from "../../utils/SectionProps";
import SectionHeader from "./partials/SectionHeader";
import Input from "../elements/Input";
import Button from "../elements/Button";


const SuperfluidSDK = require("@superfluid-finance/js-sdk");
const { Web3Provider } = require("@ethersproject/providers");


const propTypes = {
  ...SectionProps.types,
};

const defaultProps = {
  ...SectionProps.defaults,
};

class LoginForm extends React.Component {

  state = {
    contractAddress: '',
    recipientAddress: '',
    tokenStream: '',
    frequency: ''
  }

  handleChange = event => {
    console.log(event);
    this.setState({ content: event.target.value });
   }

  render() {
    const {
      className,
      topOuterDivider,
      bottomOuterDivider,
      topDivider,
      bottomDivider,
      hasBgColor,
      invertColor,
      ...props
    } = this.props;

    const outerClasses = classNames(
      "signin section",
      topOuterDivider && "has-top-divider",
      bottomOuterDivider && "has-bottom-divider",
      hasBgColor && "has-bg-color",
      invertColor && "invert-color",
      className
    );

    const innerClasses = classNames(
      "signin-inner section-inner",
      topDivider && "has-top-divider",
      bottomDivider && "has-bottom-divider"
    );

    const sectionHeader = {
      title: "KPI Options Token Distribution via Superfluid Streams",
    };

    return (
      <section {...props} className={outerClasses}>
        <div className="container">
          <div>
            <SectionHeader
              tag="h3"
              data={sectionHeader}
              className="center-content"
            />
            <div className="tiles-wrap">
              <div className="tiles-item">
                <div className="tiles-item-inner">
                  <form>
                    <fieldset>
                      <div className="mb-12">
                        <Input
                          type="input"
                          label="Contract Address"
                          placeholder="Contract Address"
                          onChange={this.handleChange}
                          required
                        />
                      </div>

                      <div className="mb-12">
                        <Input
                          type="input"
                          label="Recipient Address"
                          placeholder="Recipient Address"
                          required
                          onChange={this.handleChange}
                        />
                      </div>
                      <label className="form-label"> Token Stream </label>
                      <div
                        style={{
                          display: "flex",
                          flexDirection: "row",
                          gridTemplateColumns: "6fr 6fr",
                        }}
                        className="mb-12"
                      >
                        <Input
                          type="input"
                          placeholder="Tokens"
                          required
                          onChange={this.handleChange}
                        />
                      <select onChage={this.handleChange} style={{width: "40%"}}> 
                        <option>day</option>
                        <option> week </option>
                        <option> month </option>
                        <option>year</option>
                      </select>
                      </div>
                      <div className="mt-24 mb-32">
                        <Button color="primary" wide>
                          Start Stream
                        </Button>
                      </div>
                    </fieldset>
                  </form>
                  <div className="signin-bottom has-top-divider">
                    <div className="pt-32 text-xs center-content text-color-low">
                     <pre>
                       You can trigger stream <br/> 
                       using any other methods as well. <br/>
                       <br/>Check Documentation 
                        </pre>

                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>
    );
  }
}

LoginForm.propTypes = propTypes;
LoginForm.defaultProps = defaultProps;

export default LoginForm;
