import React from "react";
import classNames from "classnames";
import { SectionProps } from "../../utils/SectionProps";
import SectionHeader from "./partials/SectionHeader";
import Input from "../elements/Input";
import Button from "../elements/Button";
const axios = require("axios");
const SuperfluidSDK = require("@superfluid-finance/js-sdk");
const Web3 = require("web3");

const propTypes = {
  ...SectionProps.types,
};

const defaultProps = {
  ...SectionProps.defaults,
};

class LoginForm extends React.Component {
  state = {
    contractAddress: "",
    recipientAddress: "",
    walletAddress: "",
  };

  async componentDidMount() {
    const sf = new SuperfluidSDK.Framework({
      web3: new Web3(window.ethereum),
    });   
    await sf.initialize()
    const walletAddress = await window.ethereum.request({
      method: 'eth_requestAccounts',
      params: [
        {
          eth_accounts: {}
        }
      ]
    });
    console.log("wallet-address", walletAddress);
    this.setState({walletAddress: walletAddress[0]});
  }

  handleSubmit = async (event) => {
    event.preventDefault();
    console.log(this.state);

    let recipients = [];

    axios
    .get("https://run.mocky.io/v3/2e64e74b-b232-431c-804a-8a5b0b0bf113")
    .then(function (response) {
      let total = response.data.total_kpi_target;
      let recipeints = response.data.recipients;

      console.log("api json", response.data);
    })
    .catch(function (error) {
      // handle error
      console.log(error);
    })
    .then(function () {
      // always executed
    });

    const carol = sf.user({
      address: this.state.walletAddress,
      token: "0xF2d68898557cCb2Cf4C10c3Ef2B034b2a69DAD00",
    });

    await carol.flow({
      recipient: "0x52DBDfa46Cd6AeC23e248c3D2cec723830D227CD",
      flowRate: "385802469135802",
    });


    // window.ethereum.enable();

    // const sf = new SuperfluidSDK.Framework({
    //   ethers: new Web3Provider(window.ethereum),
    // });

    // await sf.initialize();

    // const walletAddress = await window.ethereum.request({
    //   method: "eth_requestAccounts",
    //   params: [
    //     {
    //       eth_accounts: {},
    //     },
    //   ],
    // });

    // const carol = sf.user({
    //   address: walletAddress[0],
    //   token: "0xd9Eb07994820dF2798FB335254d468246f83AB6C",
    // });

    // let details = await carol.details();
    // console.log(details);

    // await carol.flow({
    //   recipient: "0x230eBd3F5F443Dc8d64fA3c01f242b4F880E07c2",
    //   flowRate: "385802469135802",
    // });
    // details = await carol.details();
    // console.log(details);

  };

  render() {
    const {
      className,
      topOuterDivider,
      bottomOuterDivider,
      topDivider,
      bottomDivider,
      hasBgColor,
      invertColor,
      ...props
    } = this.props;

    const outerClasses = classNames(
      "signin section",
      topOuterDivider && "has-top-divider",
      bottomOuterDivider && "has-bottom-divider",
      hasBgColor && "has-bg-color",
      invertColor && "invert-color",
      className
    );

    const sectionHeader = {
      title: "KPI Options Token Distribution via Superfluid Streams",
    };

    return (
      <section {...props} className={outerClasses}>
        <div className="container">
          <div>
            <SectionHeader
              tag="h3"
              data={sectionHeader}
              className="center-content"
            />
            <div className="tiles-wrap">
              <div className="tiles-item">
                <div className="tiles-item-inner">
                  <form onSubmit={this.handleSubmit}>
                    <fieldset>
                      <div className="mb-12">
                        <Input
                          type="input"
                          label="ERC 20 Super Token Address"
                          placeholder="ERC 20 Super Token Address"
                          onChange={(e) =>
                            this.setState({ contractAddress: e.target.value })
                          }
                          required
                          state="contractAddress"
                        />
                      </div>

                      <div className="mt-24 mb-32">
                        <Button type="submit" color="primary" wide>
                          Distribute Long Tokens Instantly
                        </Button>
                      </div>
                    </fieldset>
                  </form>
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>
    );
  }
}

LoginForm.propTypes = propTypes;
LoginForm.defaultProps = defaultProps;

export default LoginForm;
